import React, { useState } from "react";
import { Questions } from "./Accordianapi";

const Accordian = () => {

    const [data, setdata] = useState(Questions);

    const [show, setshow] = useState(false);
    const setmedia = () => {
        setshow(true);
    }

    return (
        <>
            {
                data.map((val) => {
                    const { id, question, answer } = val;
                    return (
                        <>
                            <div 
                        key={id}>
                                <h1 className="text-centre">
                                    <button onClick={setmedia}> + </button> {question} <br />
                                </h1>
                            </div>
                            {
                                show && <p> {answer} <br /></p>
                            }

                        </>)
                })
            }
        </>
    )
}

export default Accordian;
