import React, { useState } from "react";

const Todolist=()=>{

    const [items,fitems]=useState("");
    const [ilist,flist]=useState([]);

    const inputlist=(event)=>{
        const val=fitems(event.target.value);
    }

    const additems=()=>{
        if( val.value===0){
            return(alert("please add something"))
        }
        flist((olditems)=>{
            return[...olditems,items]
        });
        fitems('')
    };

    return(
        <>
            <h1>ToDo List{}</h1>
            <input 
                type="Text"
                value={items}
                placeholder="Add Items Name Here"
                onChange={inputlist}
            />
            <button onClick={additems}>+</button><br/>
            
            {
                ilist.map((value)=>{
                    return(<li>{value}</li>)
                })
            }
           
        </>
    )
}
export default Todolist;
