import React, { useState } from "react";

const Todolist=()=>{

    const [items,fitems]=useState("");
    const [ilist,flist]=useState([]);

    const inputlist=(event)=>{
        fitems(event.target.value);
    }

    const additems=()=>{
        if( inputlist.value===""){
            console.log("dshdsjdbs")
            return(alert("please add something"))
        }
        flist((olditems)=>{
            return[...olditems,items]
        });
        fitems('')
    };
    const deleteitems=()=>{
        // console.log("deleted")
    }

    return(
        <>
            <h1>ToDo List{}</h1>
            <input 
                type="Text"
                value={items}
                placeholder="Add Items Name Here"
                onChange={inputlist}
            />
            <button onClick={additems}>+</button><br/>
            
            {
                ilist.map((value)=>{
                    return(<><li>{value}</li>
                    <div>
                    <button onClick={deleteitems}>
                        
                    <button/>*</button></div></>)
                })
            }
           
        </>
    )
}
export default Todolist;
