import React from "react";
import Accordian from "./Accordian";

const Daccordian = () => {

    const [data, setdata] = useState(Questions);

    const [show, setshow] = useState(false);
    const setmedia = () => {
        setshow(!show);
    }


    return (
        <>
            {
                data.map((val) => {
                    const { id, question, answer } = val;
                    return (
                        <div id={id}>
                            <div>
                                <h1 className="text-centre">
                                    <button onClick={setmedia}>{show ? "➖" : "➕"}</button> {question} <br />
                                </h1>
                            </div>
                            {
                                show && <p
                                    key={id}> {answer} <br /></p>
                            }

                        </div>)
                })
            }
        </>
    )
}

export default Daccordian;
