import React, { useState } from 'react';


const Clock = () => {
    var time = new Date().toLocaleTimeString();
    const [ctime, updated] = useState(time);
    const reload=()=>{
        time = new Date().toLocaleTimeString()
        updated(time);
    }
      
    return(
        <>
            {time}<br/>
            <button onClick={reload}>Get time</button>
        </>
    )
}

export default Clock;
