import React, { useState } from "react";
import { Questions } from "./Accordianapi";

const Accordian = () => {

    const [data, setdata] = useState(Questions);

    return (
        <>
            {
                data.map((val) => {
                    const { id, question, answer } = val;
                    return (
                        <>
                        <h1 className="text-centre">
                            +    {question} <br /></h1>
                            {/* {answer} <br /> */}
                        </>)
                })
            }
        </>
    )
}

export default Accordian;
